import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    Grid,
    Card,
    CardContent,
    CardActionArea,
    Box,
    CircularProgress,
    useTheme,
    Button
} from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function SuggestedPokemon({ getTypeColor }) {
    const [suggestedPokemon, setSuggestedPokemon] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();
    const theme = useTheme();

    const fetchSuggestedPokemon = async () => {
        setLoading(true);
        setError('');
        try {
            const lastViewedPokemon = JSON.parse(localStorage.getItem('selectedPokemon'));
            let response;

            if (lastViewedPokemon) {
                // Get suggestions based on last viewed Pokémon's type
                response = await axios.get(`http://localhost:3001/api/pokemon/suggested/${encodeURIComponent(lastViewedPokemon['Primary Type'])}?currentPokemon=${encodeURIComponent(lastViewedPokemon.Name)}`);
            } else {
                // Get random Pokémon suggestions
                response = await axios.get('http://localhost:3001/api/pokemon/random');
            }

            if (response.data && Array.isArray(response.data)) {
                setSuggestedPokemon(response.data);
            } else {
                throw new Error('Invalid response format');
            }
        } catch (error) {
            console.error('Error fetching pokemon:', error);
            setError('Failed to fetch Pokémon. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchSuggestedPokemon();
    }, []);

    const handlePokemonClick = (poke) => {
        localStorage.setItem('selectedPokemon', JSON.stringify(poke));
        navigate(`/pokemon/${encodeURIComponent(poke.Name)}`);
    };

    const handleRetry = () => {
        fetchSuggestedPokemon();
    };

    return (
        <Container maxWidth="lg" sx={{ py: 4 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 4 }}>
                <Typography
                    variant="h4"
                    sx={{
                        fontWeight: 'bold',
                        color: theme.palette.text.primary
                    }}
                >
                    {localStorage.getItem('selectedPokemon') ? 'Suggested Pokémon' : 'Discover Random Pokémon'}
                </Typography>
                <Button
                    variant="contained"
                    color="primary"
                    startIcon={<RefreshIcon />}
                    onClick={handleRetry}
                    disabled={loading}
                    sx={{
                        borderRadius: 2,
                        textTransform: 'none'
                    }}
                >
                    Refresh
                </Button>
            </Box>

            {loading ? (
                <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
                    <CircularProgress />
                </Box>
            ) : error ? (
                <Box sx={{ textAlign: 'center', my: 4 }}>
                    <Typography color="error" gutterBottom>
                        {error}
                    </Typography>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleRetry}
                        startIcon={<RefreshIcon />}
                        sx={{ mt: 2 }}
                    >
                        Try Again
                    </Button>
                </Box>
            ) : (
                <Grid container spacing={3}>
                    {suggestedPokemon.map((poke) => (
                        <Grid item xs={12} sm={6} md={4} key={poke.Name + poke.Name2}>
                            <Card
                                sx={{
                                    height: '100%',
                                    transition: 'all 0.3s ease',
                                    background: `linear-gradient(135deg, ${getTypeColor(poke['Primary Type'])}80 0%, ${getTypeColor(poke['Primary Type'])}30 100%)`,
                                    borderRadius: 4,
                                    border: `2px solid ${getTypeColor(poke['Primary Type'])}`,
                                    '&:hover': {
                                        transform: 'translateY(-5px)',
                                        boxShadow: `0 8px 16px ${getTypeColor(poke['Primary Type'])}90`,
                                        borderColor: getTypeColor(poke['Primary Type'])
                                    }
                                }}
                            >
                                <CardActionArea onClick={() => handlePokemonClick(poke)}>
                                    <CardContent>
                                        <Typography
                                            variant="h5"
                                            component="h2"
                                            gutterBottom
                                            sx={{
                                                color: '#fff',
                                                fontWeight: 'bold',
                                                textShadow: `2px 2px 4px ${getTypeColor(poke['Primary Type'])}`
                                            }}
                                        >
                                            {poke.Name} {poke.Name2 && `(${poke.Name2})`}
                                        </Typography>
                                        <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                                            <Typography
                                                sx={{
                                                    bgcolor: getTypeColor(poke['Primary Type']),
                                                    color: 'white',
                                                    px: 2,
                                                    py: 1,
                                                    borderRadius: 2,
                                                    fontSize: '1rem',
                                                    fontWeight: 'bold',
                                                    boxShadow: `0 2px 4px ${getTypeColor(poke['Primary Type'])}90`
                                                }}
                                            >
                                                {poke['Primary Type']}
                                            </Typography>
                                            {poke['Secondary type'] && (
                                                <Typography
                                                    sx={{
                                                        bgcolor: getTypeColor(poke['Secondary type']),
                                                        color: 'white',
                                                        px: 2,
                                                        py: 1,
                                                        borderRadius: 2,
                                                        fontSize: '1rem',
                                                        fontWeight: 'bold',
                                                        boxShadow: `0 2px 4px ${getTypeColor(poke['Secondary type'])}90`
                                                    }}
                                                >
                                                    {poke['Secondary type']}
                                                </Typography>
                                            )}
                                        </Box>
                                        <Typography variant="body2" component="div">
                                            <Grid container spacing={1}>
                                                <Grid item xs={6}>
                                                    <Typography sx={{ color: '#fff', textShadow: `1px 1px 2px ${getTypeColor(poke['Primary Type'])}` }}>
                                                        <strong>HP:</strong> {poke.HP}
                                                    </Typography>
                                                </Grid>
                                                <Grid item xs={6}>
                                                    <Typography sx={{ color: '#fff', textShadow: `1px 1px 2px ${getTypeColor(poke['Primary Type'])}` }}>
                                                        <strong>Attack:</strong> {poke.Attack}
                                                    </Typography>
                                                </Grid>
                                                <Grid item xs={6}>
                                                    <Typography sx={{ color: '#fff', textShadow: `1px 1px 2px ${getTypeColor(poke['Primary Type'])}` }}>
                                                        <strong>Defense:</strong> {poke.Defense}
                                                    </Typography>
                                                </Grid>
                                                <Grid item xs={6}>
                                                    <Typography sx={{ color: '#fff', textShadow: `1px 1px 2px ${getTypeColor(poke['Primary Type'])}` }}>
                                                        <strong>Speed:</strong> {poke.Speed}
                                                    </Typography>
                                                </Grid>
                                            </Grid>
                                        </Typography>
                                    </CardContent>
                                </CardActionArea>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            )}
        </Container>
    );
}

export default SuggestedPokemon;
