import React, { useState, useEffect } from 'react';
import {
    Container,
    TextField,
    Grid,
    Card,
    CardContent,
    Typography,
    Select,
    MenuItem,
    FormControl,
    InputLabel,
    Box,
    CircularProgress,
    Alert,
    Snackbar
} from '@mui/material';
import axios from 'axios';

function App() {
    const [pokemon, setPokemon] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedType, setSelectedType] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const types = ['GRASS', 'POISON', 'FIRE', 'FLYING', 'DRAGON', 'WATER', 'BUG', 'NORMAL', 'ELECTRIC', 'GROUND', 'FAIRY', 'FIGHTING', 'PSYCHIC', 'ROCK', 'STEEL', 'ICE', 'GHOST', 'DARK'];

    useEffect(() => {
        fetchPokemon();
    }, []);

    const fetchPokemon = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.get('http://localhost:3001/api/pokemon');
            setPokemon(response.data);
        } catch (error) {
            console.error('Error fetching pokemon:', error);
            setError('Failed to fetch Pokemon. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = async (event) => {
        const value = event.target.value;
        setSearchTerm(value);
        setLoading(true);
        setError('');

        try {
            if (value) {
                const response = await axios.get(`http://localhost:3001/api/pokemon/search?name=${value}`);
                setPokemon(response.data);
            } else {
                fetchPokemon();
            }
        } catch (error) {
            console.error('Error searching pokemon:', error);
            setError('Failed to search Pokemon. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleTypeChange = async (event) => {
        const value = event.target.value;
        setSelectedType(value);
        setLoading(true);
        setError('');

        try {
            if (value) {
                const response = await axios.get(`http://localhost:3001/api/pokemon/type/${value}`);
                setPokemon(response.data);
            } else {
                fetchPokemon();
            }
        } catch (error) {
            console.error('Error filtering by type:', error);
            setError('Failed to filter Pokemon by type. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const getTypeColor = (type) => {
        const colors = {
            GRASS: '#78C850',
            POISON: '#A040A0',
            FIRE: '#F08030',
            FLYING: '#A890F0',
            DRAGON: '#7038F8',
            WATER: '#6890F0',
            BUG: '#A8B820',
            NORMAL: '#A8A878',
            ELECTRIC: '#F8D030',
            GROUND: '#E0C068',
            FAIRY: '#EE99AC',
            FIGHTING: '#C03028',
            PSYCHIC: '#F85888',
            ROCK: '#B8A038',
            STEEL: '#B8B8D0',
            ICE: '#98D8D8',
            GHOST: '#705898',
            DARK: '#705848'
        };
        return colors[type] || '#68A090';
    };

    return (
        <Container maxWidth="lg" sx={{ py: 4 }}>
            <Typography
                variant="h3"
                component="h1"
                gutterBottom
                align="center"
                sx={{
                    fontWeight: 'bold',
                    color: '#2F6690',
                    textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
                }}
            >
                Pokemon Database
            </Typography>

            <Box sx={{ mb: 4, display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                <TextField
                    label="Search Pokemon"
                    variant="outlined"
                    value={searchTerm}
                    onChange={handleSearch}
                    sx={{ flexGrow: 1, minWidth: '200px' }}
                />

                <FormControl sx={{ minWidth: 200 }}>
                    <InputLabel>Filter by Type</InputLabel>
                    <Select
                        value={selectedType}
                        label="Filter by Type"
                        onChange={handleTypeChange}
                    >
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        {types.map((type) => (
                            <MenuItem
                                key={type}
                                value={type}
                                sx={{
                                    backgroundColor: `${getTypeColor(type)}20`,
                                    '&:hover': {
                                        backgroundColor: `${getTypeColor(type)}40`
                                    }
                                }}
                            >
                                {type}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
            </Box>

            {loading ? (
                <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
                    <CircularProgress />
                </Box>
            ) : (
                <Grid container spacing={3}>
                    {pokemon.map((poke) => (
                        <Grid item xs={12} sm={6} md={4} key={poke.Name + poke.Name2}>
                            <Card
                                sx={{
                                    height: '100%',
                                    transition: 'transform 0.2s',
                                    '&:hover': {
                                        transform: 'translateY(-5px)',
                                        boxShadow: 3
                                    }
                                }}
                            >
                                <CardContent>
                                    <Typography variant="h5" component="h2" gutterBottom>
                                        {poke.Name} {poke.Name2 && `(${poke.Name2})`}
                                    </Typography>
                                    <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                                        <Typography
                                            sx={{
                                                bgcolor: getTypeColor(poke.Primary_Type),
                                                color: 'white',
                                                px: 1,
                                                py: 0.5,
                                                borderRadius: 1,
                                                fontSize: '0.875rem'
                                            }}
                                        >
                                            {poke.Primary_Type}
                                        </Typography>
                                        {poke.Secondary_type && (
                                            <Typography
                                                sx={{
                                                    bgcolor: getTypeColor(poke.Secondary_type),
                                                    color: 'white',
                                                    px: 1,
                                                    py: 0.5,
                                                    borderRadius: 1,
                                                    fontSize: '0.875rem'
                                                }}
                                            >
                                                {poke.Secondary_type}
                                            </Typography>
                                        )}
                                    </Box>
                                    <Typography variant="body2" component="div">
                                        <Grid container spacing={1}>
                                            <Grid item xs={6}>
                                                <strong>HP:</strong> {poke.HP}
                                            </Grid>
                                            <Grid item xs={6}>
                                                <strong>Attack:</strong> {poke.Attack}
                                            </Grid>
                                            <Grid item xs={6}>
                                                <strong>Defense:</strong> {poke.Defense}
                                            </Grid>
                                            <Grid item xs={6}>
                                                <strong>Speed:</strong> {poke.Speed}
                                            </Grid>
                                            <Grid item xs={6}>
                                                <strong>Sp.Attack:</strong> {poke.Sp_Attack}
                                            </Grid>
                                            <Grid item xs={6}>
                                                <strong>Sp.Defense:</strong> {poke.Sp_Defense}
                                            </Grid>
                                            <Grid item xs={12}>
                                                <Typography
                                                    sx={{
                                                        mt: 1,
                                                        fontWeight: 'bold',
                                                        color: '#2F6690'
                                                    }}
                                                >
                                                    Total: {poke.Total}
                                                </Typography>
                                            </Grid>
                                        </Grid>
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            )}

            <Snackbar
                open={!!error}
                autoHideDuration={6000}
                onClose={() => setError('')}
            >
                <Alert
                    onClose={() => setError('')}
                    severity="error"
                    sx={{ width: '100%' }}
                >
                    {error}
                </Alert>
            </Snackbar>
        </Container>
    );
}

export default App;
