import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
    Container,
    Paper,
    Typography,
    Box,
    Grid,
    Button,
    LinearProgress,
    Chip
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

function PokemonDisplay({ getTypeColor }) {
    const { pokemonName } = useParams();
    const navigate = useNavigate();
    const pokemon = JSON.parse(localStorage.getItem('selectedPokemon'));
    const mainColor = pokemon ? getTypeColor(pokemon.Primary_Type) : '#2F6690';
    const secondaryColor = pokemon ? `${getTypeColor(pokemon.Primary_Type)}20` : '#2F669020';

    const StatBar = ({ label, value, maxValue = 255 }) => {
        const percentage = (value / maxValue) * 100;
        let barColor = mainColor;

        // Adjust bar color based on stat value
        if (percentage < 30) barColor = '#ff6b6b';
        else if (percentage < 60) barColor = '#ffd93d';
        else if (percentage >= 80) barColor = '#6bff6b';

        return (
            <Box sx={{ width: '100%', mb: 2 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                    <Typography variant="body2" color="text.secondary">
                        {label}
                    </Typography>
                    <Typography
                        variant="body2"
                        sx={{
                            fontWeight: 'bold',
                            color: barColor
                        }}
                    >
                        {value}
                    </Typography>
                </Box>
                <LinearProgress
                    variant="determinate"
                    value={percentage}
                    sx={{
                        height: 10,
                        borderRadius: 5,
                        backgroundColor: 'rgba(0,0,0,0.1)',
                        '& .MuiLinearProgress-bar': {
                            backgroundColor: barColor
                        }
                    }}
                />
            </Box>
        );
    };

    if (!pokemon) {
        return (
            <Container maxWidth="md" sx={{ py: 4 }}>
                <Typography variant="h5" align="center">
                    Pokemon not found
                </Typography>
                <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
                    <Button
                        variant="contained"
                        startIcon={<ArrowBackIcon />}
                        onClick={() => navigate('/')}
                        sx={{
                            bgcolor: mainColor,
                            '&:hover': {
                                bgcolor: `${mainColor}dd`
                            }
                        }}
                    >
                        Back to List
                    </Button>
                </Box>
            </Container>
        );
    }

    return (
        <Container maxWidth="md" sx={{ py: 4 }}>
            <Button
                variant="outlined"
                startIcon={<ArrowBackIcon />}
                onClick={() => navigate('/')}
                sx={{
                    mb: 3,
                    color: mainColor,
                    borderColor: mainColor,
                    '&:hover': {
                        borderColor: mainColor,
                        backgroundColor: secondaryColor
                    }
                }}
            >
                Back to List
            </Button>

            <Paper
                elevation={3}
                sx={{
                    p: 4,
                    borderRadius: 4,
                    background: `linear-gradient(135deg, white 60%, ${secondaryColor} 100%)`
                }}
            >
                <Typography
                    variant="h3"
                    component="h1"
                    gutterBottom
                    align="center"
                    sx={{
                        fontWeight: 'bold',
                        color: mainColor,
                        mb: 4,
                        textShadow: `2px 2px 4px ${secondaryColor}`
                    }}
                >
                    {pokemon.Name} {pokemon.Name2 && `(${pokemon.Name2})`}
                </Typography>

                <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mb: 4 }}>
                    <Chip
                        label={pokemon.Primary_Type}
                        sx={{
                            bgcolor: getTypeColor(pokemon.Primary_Type),
                            color: 'white',
                            fontSize: '1rem',
                            py: 2,
                            px: 1
                        }}
                    />
                    {pokemon.Secondary_type && (
                        <Chip
                            label={pokemon.Secondary_type}
                            sx={{
                                bgcolor: getTypeColor(pokemon.Secondary_type),
                                color: 'white',
                                fontSize: '1rem',
                                py: 2,
                                px: 1
                            }}
                        />
                    )}
                </Box>

                <Grid container spacing={4}>
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={2}
                            sx={{
                                p: 3,
                                borderRadius: 3,
                                height: '100%',
                                border: `1px solid ${secondaryColor}`
                            }}
                        >
                            <Typography variant="h6" gutterBottom sx={{ color: mainColor }}>
                                Base Stats
                            </Typography>
                            <StatBar label="HP" value={pokemon.HP} />
                            <StatBar label="Attack" value={pokemon.Attack} />
                            <StatBar label="Defense" value={pokemon.Defense} />
                            <StatBar label="Speed" value={pokemon.Speed} />
                            <StatBar label="Sp. Attack" value={pokemon.Sp_Attack} />
                            <StatBar label="Sp. Defense" value={pokemon.Sp_Defense} />
                        </Paper>
                    </Grid>
                    <Grid item xs={12} md={6}>
                        <Paper
                            elevation={2}
                            sx={{
                                p: 3,
                                borderRadius: 3,
                                height: '100%',
                                border: `1px solid ${secondaryColor}`,
                                position: 'relative',
                                overflow: 'hidden',
                                '&::after': {
                                    content: '""',
                                    position: 'absolute',
                                    top: 0,
                                    right: 0,
                                    width: '150px',
                                    height: '150px',
                                    background: `radial-gradient(circle, ${secondaryColor} 0%, transparent 70%)`,
                                    opacity: 0.5
                                }
                            }}
                        >
                            <Typography variant="h6" gutterBottom sx={{ color: mainColor }}>
                                Total Stats
                            </Typography>
                            <Box
                                sx={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    height: '80%'
                                }}
                            >
                                <Typography
                                    variant="h2"
                                    sx={{
                                        fontWeight: 'bold',
                                        color: mainColor
                                    }}
                                >
                                    {pokemon.Total}
                                </Typography>
                            </Box>
                        </Paper>
                    </Grid>
                </Grid>
            </Paper>
        </Container>
    );
}

export default PokemonDisplay;
