import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
    Container,
    Paper,
    Typography,
    Box,
    Grid,
    Button,
    LinearProgress,
    Chip,
    Fade,
    Zoom
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import WhatshotIcon from '@mui/icons-material/Whatshot';

function PokemonDisplay({ getTypeColor }) {
    const { pokemonName } = useParams();
    const navigate = useNavigate();
    const pokemon = JSON.parse(localStorage.getItem('selectedPokemon'));
    const mainColor = pokemon ? getTypeColor(pokemon.Primary_Type) : '#2F6690';
    const secondaryColor = pokemon ? `${getTypeColor(pokemon.Primary_Type)}20` : '#2F669020';

    const StatBar = ({ label, value, maxValue = 255, index }) => {
        const percentage = (value / maxValue) * 100;
        let barColor = mainColor;

        // Adjust bar color based on stat value
        if (percentage < 30) barColor = '#ff6b6b';
        else if (percentage < 60) barColor = '#ffd93d';
        else if (percentage >= 80) barColor = '#6bff6b';

        return (
            <Fade in timeout={500 + index * 100}>
                <Box sx={{ width: '100%', mb: 2 }}>
                    <Box sx={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        mb: 0.5,
                        alignItems: 'center'
                    }}>
                        <Typography
                            variant="body1"
                            sx={{
                                color: mainColor,
                                fontWeight: 500,
                                textTransform: 'uppercase',
                                letterSpacing: '0.05em',
                                fontSize: '0.875rem'
                            }}
                        >
                            {label}
                        </Typography>
                        <Typography
                            variant="body1"
                            sx={{
                                fontWeight: 'bold',
                                color: barColor,
                                fontSize: '1rem',
                                display: 'flex',
                                alignItems: 'center',
                                gap: 0.5
                            }}
                        >
                            {value}
                            {percentage >= 80 && <WhatshotIcon sx={{ fontSize: '1rem', color: '#ff4444' }} />}
                        </Typography>
                    </Box>
                    <Box sx={{ position: 'relative', width: '100%' }}>
                        <LinearProgress
                            variant="determinate"
                            value={percentage}
                            sx={{
                                height: 12,
                                borderRadius: 6,
                                backgroundColor: 'rgba(0,0,0,0.05)',
                                boxShadow: 'inset 0 1px 2px rgba(0,0,0,0.1)',
                                '& .MuiLinearProgress-bar': {
                                    backgroundColor: barColor,
                                    backgroundImage: 'linear-gradient(45deg, rgba(255,255,255,0.15) 25%, transparent 25%, transparent 50%, rgba(255,255,255,0.15) 50%, rgba(255,255,255,0.15) 75%, transparent 75%, transparent)',
                                    backgroundSize: '1rem 1rem',
                                    animation: 'progress-bar-stripes 1s linear infinite',
                                },
                                '@keyframes progress-bar-stripes': {
                                    '0%': { backgroundPosition: '1rem 0' },
                                    '100%': { backgroundPosition: '0 0' }
                                }
                            }}
                        />
                        <Box
                            sx={{
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                boxShadow: '0 1px 1px rgba(255,255,255,0.4) inset',
                                borderRadius: 6,
                                pointerEvents: 'none'
                            }}
                        />
                    </Box>
                </Box>
            </Fade>
        );
    };

    if (!pokemon) {
        return (
            <Container maxWidth="md" sx={{ py: 4 }}>
                <Typography variant="h5" align="center">
                    Pokemon not found
                </Typography>
                <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
                    <Button
                        variant="contained"
                        startIcon={<ArrowBackIcon />}
                        onClick={() => navigate('/')}
                        sx={{
                            bgcolor: mainColor,
                            '&:hover': {
                                bgcolor: `${mainColor}dd`
                            }
                        }}
                    >
                        Back to List
                    </Button>
                </Box>
            </Container>
        );
    }

    return (
        <Container maxWidth="md" sx={{ py: 4 }}>
            <Fade in timeout={300}>
                <Button
                    variant="outlined"
                    startIcon={<ArrowBackIcon />}
                    onClick={() => navigate('/')}
                    sx={{
                        mb: 3,
                        color: mainColor,
                        borderColor: mainColor,
                        borderWidth: 2,
                        '&:hover': {
                            borderColor: mainColor,
                            borderWidth: 2,
                            backgroundColor: secondaryColor,
                            transform: 'translateX(-5px)'
                        },
                        transition: 'all 0.3s ease'
                    }}
                >
                    Back to List
                </Button>
            </Fade>

            <Zoom in timeout={500}>
                <Paper
                    elevation={8}
                    sx={{
                        p: 4,
                        borderRadius: 4,
                        background: `linear-gradient(135deg, white 60%, ${secondaryColor} 100%)`,
                        position: 'relative',
                        overflow: 'hidden',
                        '&::before': {
                            content: '""',
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: `radial-gradient(circle at top right, ${secondaryColor} 0%, transparent 70%)`,
                            opacity: 0.4
                        }
                    }}
                >
                    <Box sx={{ position: 'relative' }}>
                        <Typography
                            variant="h3"
                            component="h1"
                            gutterBottom
                            align="center"
                            sx={{
                                fontWeight: 900,
                                color: mainColor,
                                mb: 4,
                                textShadow: `2px 2px 4px ${secondaryColor}`,
                                letterSpacing: '-0.02em',
                                position: 'relative',
                                '&::after': {
                                    content: '""',
                                    position: 'absolute',
                                    bottom: -8,
                                    left: '50%',
                                    transform: 'translateX(-50%)',
                                    width: '60px',
                                    height: '4px',
                                    backgroundColor: mainColor,
                                    borderRadius: '2px'
                                }
                            }}
                        >
                            {pokemon.Name} {pokemon.Name2 && `(${pokemon.Name2})`}
                        </Typography>

                        <Box sx={{
                            display: 'flex',
                            justifyContent: 'center',
                            gap: 2,
                            mb: 4,
                            '& .MuiChip-root': {
                                transition: 'all 0.3s ease',
                                '&:hover': {
                                    transform: 'scale(1.1)',
                                    boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
                                }
                            }
                        }}>
                            <Chip
                                label={pokemon.Primary_Type}
                                sx={{
                                    bgcolor: getTypeColor(pokemon.Primary_Type),
                                    color: 'white',
                                    fontSize: '1rem',
                                    py: 2.5,
                                    px: 1,
                                    fontWeight: 'bold',
                                    letterSpacing: '0.05em'
                                }}
                            />
                            {pokemon.Secondary_type && (
                                <Chip
                                    label={pokemon.Secondary_type}
                                    sx={{
                                        bgcolor: getTypeColor(pokemon.Secondary_type),
                                        color: 'white',
                                        fontSize: '1rem',
                                        py: 2.5,
                                        px: 1,
                                        fontWeight: 'bold',
                                        letterSpacing: '0.05em'
                                    }}
                                />
                            )}
                        </Box>

                        <Grid container spacing={4}>
                            <Grid item xs={12} md={6}>
                                <Paper
                                    elevation={4}
                                    sx={{
                                        p: 3,
                                        borderRadius: 3,
                                        height: '100%',
                                        border: `2px solid ${secondaryColor}`,
                                        transition: 'all 0.3s ease',
                                        '&:hover': {
                                            transform: 'translateY(-5px)',
                                            boxShadow: `0 8px 16px ${secondaryColor}`
                                        }
                                    }}
                                >
                                    <Typography
                                        variant="h6"
                                        gutterBottom
                                        sx={{
                                            color: mainColor,
                                            fontWeight: 'bold',
                                            letterSpacing: '0.05em',
                                            position: 'relative',
                                            '&::after': {
                                                content: '""',
                                                position: 'absolute',
                                                bottom: -4,
                                                left: 0,
                                                width: '40px',
                                                height: '3px',
                                                backgroundColor: mainColor,
                                                borderRadius: '1.5px'
                                            }
                                        }}
                                    >
                                        Base Stats
                                    </Typography>
                                    <Box sx={{ mt: 3 }}>
                                        <StatBar label="HP" value={pokemon.HP} index={0} />
                                        <StatBar label="Attack" value={pokemon.Attack} index={1} />
                                        <StatBar label="Defense" value={pokemon.Defense} index={2} />
                                        <StatBar label="Speed" value={pokemon.Speed} index={3} />
                                        <StatBar label="Sp. Attack" value={pokemon.Sp_Attack} index={4} />
                                        <StatBar label="Sp. Defense" value={pokemon.Sp_Defense} index={5} />
                                    </Box>
                                </Paper>
                            </Grid>
                            <Grid item xs={12} md={6}>
                                <Paper
                                    elevation={4}
                                    sx={{
                                        p: 3,
                                        borderRadius: 3,
                                        height: '100%',
                                        border: `2px solid ${secondaryColor}`,
                                        position: 'relative',
                                        overflow: 'hidden',
                                        transition: 'all 0.3s ease',
                                        '&:hover': {
                                            transform: 'translateY(-5px)',
                                            boxShadow: `0 8px 16px ${secondaryColor}`
                                        },
                                        '&::before': {
                                            content: '""',
                                            position: 'absolute',
                                            top: 0,
                                            right: 0,
                                            width: '200px',
                                            height: '200px',
                                            background: `radial-gradient(circle, ${secondaryColor} 0%, transparent 70%)`,
                                            opacity: 0.5
                                        }
                                    }}
                                >
                                    <Typography
                                        variant="h6"
                                        gutterBottom
                                        sx={{
                                            color: mainColor,
                                            fontWeight: 'bold',
                                            letterSpacing: '0.05em',
                                            position: 'relative',
                                            '&::after': {
                                                content: '""',
                                                position: 'absolute',
                                                bottom: -4,
                                                left: 0,
                                                width: '40px',
                                                height: '3px',
                                                backgroundColor: mainColor,
                                                borderRadius: '1.5px'
                                            }
                                        }}
                                    >
                                        Total Stats
                                    </Typography>
                                    <Box
                                        sx={{
                                            display: 'flex',
                                            alignItems: 'center',
                                            justifyContent: 'center',
                                            height: '80%',
                                            position: 'relative'
                                        }}
                                    >
                                        <Fade in timeout={1000}>
                                            <Typography
                                                variant="h1"
                                                sx={{
                                                    fontWeight: 900,
                                                    color: mainColor,
                                                    textShadow: `2px 2px 4px ${secondaryColor}`,
                                                    position: 'relative',
                                                    zIndex: 1
                                                }}
                                            >
                                                {pokemon.Total}
                                            </Typography>
                                        </Fade>
                                    </Box>
                                </Paper>
                            </Grid>
                        </Grid>
                    </Box>
                </Paper>
            </Zoom>
        </Container>
    );
}

export default PokemonDisplay;
